pragma solidity ^0.4.24;

import "openzeppelin-solidity/contracts/token/ERC721/ERC721.sol";

contract CurryContract is ERC721 {
    event CurryRegistered(uint _curryId);
    event CurryBought(uint _curryId);

    struct Curry {
        string name;
        uint price;
        uint rank;
    }

    Curry[] curryList;

    mapping (uint => address) curryToOwner;
    mapping (address => uint) ownerCurryCount;

    function balanceOf(address _owner) public view returns (uint) {
        return ownerCurryCount[_owner];
    }

    function ownerOf(uint tokenId) public view returns (address) {
        return curryToOwner[tokenId];
    }

    function buyCurry(uint curryId) public payable {
        require(msg.sender != ownerOf(curryId));

        // TODO: Curry can be owned by many customers at the same time?
        curryToOwner[curryId] = msg.sender;
        ownerCurryCount[msg.sender]++;

        emit CurryBought(curryId);
    }

    function registerAsCurry(string name) public returns (uint) {
        Curry memory curry = Curry(name, 0, 0);

        uint id = curryList.push(curry);
        emit CurryRegistered(id);

        return id;
    }
}

contract CurryVIP is CurryContract {
    mapping (uint => bool) vipCurrys;

    function toggleVIP(uint curryId) public payable {
        vipCurrys[curryId] = !vipCurrys[curryId];
    }
}
